(289, 1, '2022-10-01 08:21:17', '2022-10-01 08:21:17', '<!-- wp:html -->\n<fieldset class=\"btn-follow\">\n<input id=\"btn-follow__input\" type=\"checkbox\">\n<label for=\"btn-follow__input\" class=\"btn-follow__label\">Follow</label>\n<label for=\"btn-follow__input\" class=\"btn-follow__label followed\">Followed</label>\n</fieldset>\n<style>\n.btn-follow {\n  border:none;\n  position:relative;\n  padding:0;\n  margin:0;\n}\n.btn-follow:hover{\n  opacity:0.7;\n}\n.btn-follow.loading{\n  opacity:0.5;\n  pointer-events:none;\n}\n.btn-follow.loading::after{\n  content:\"\";\n  display:block;\n  height:1.5rem;\n  width:1.5rem;\n  background:url(\"http://localhost/wordpress-theme-test/wp-content/uploads/2022/10/icon-loading.png\");\n  background-size:contain;\n  position:absolute;\n  top:0;\n  bottom:0;\n  left:0;\n  right:0;\n  margin:auto;\n  animation:loadingSpin 1s infinite;\n}\n@keyframes loadingSpin{\n  to{\n    transform:rotate(360deg);\n  }\n}\n.btn-follow__label{\n  text-align:center;\n  display: block;\n  font-family: inherit;\n  font-size: 0.9rem;\n  color: white;\n  font-weight: bold;\n  background: #6cb4ee;\n  padding: 0.5rem 0;\n  border:2px solid #6cb4ee;\n}\n.btn-follow__label.followed{\n  background:white;\n  color:#6cb4ee;\n}\n#btn-follow__input{\n  position:absolute;\n  top:0;\n  left:0;\n  right:0;\n  bottom:0;\n  margin:auto;\n  height:100%;\n  width:100%;\n  opacity:0;\n  cursor:pointer;\n}\n#btn-follow__input:not(:checked) ~ .btn-follow__label.followed{\n  display:none;\n}\n#btn-follow__input:checked + .btn-follow__label{\n  display:none;\n}\n</style>\n<script>\nconst followButton = document.querySelector(\".btn-follow\")\nconst followInput = document.querySelector(\"#btn-follow__input\")\n\nconst handleFollowInputClick = (event) => {\n  event.preventDefault() // prevent checkbox update\n  followButton.classList.add(\"loading\")\n\n  const URL = window.location.href\n  const {BASE_URL, WP_NONCE} = API_Settings\n  const API = `${BASE_URL}/wp-json${URL.slice(URL.indexOf(BASE_URL) + BASE_URL.length)}follow`\n\n  fetch(API, {\n        credentials: \"same-origin\",\n        headers: {\n            \"X-WP-Nonce\": WP_NONCE\n        }\n    })\n    .then(res => res.json())\n    .then(isChecked => {\n        followInput.checked = isChecked\n    })\n    .finally(() => {\n        followButton.classList.remove(\"loading\")\n    })\n}\n\nfollowInput .addEventListener(\"click\", handleFollowInputClick)\n</script>\n<!-- /wp:html -->', 'Follow Button', '', 'publish', 'closed', 'closed', '', 'follow-button', '', '', '2022-10-21 14:51:31', '2022-10-21 14:51:31', '', 0, 'http://localhost/wordpress-theme-test/follow-button/', 0, 'wp_block', '', 0),
